import React from "react";
import { useProductsContext } from "../context/products_context";
import { Link } from "react-router-dom";
import styled from "styled-components";
import Error from "./Error";
import Loading from "./Loading";
import Product from "./Product";
import bootstrap from 'bootstrap\dist\css\bootstrap.min.css';

const FeaturedProducts = () => {
  const {
    products_loading: loading,
    products_error: error,
    featured_products: featured,
  } = useProductsContext();
  if (loading) {
    return <Loading />;
  }
  if (error) {
    return <Error />;
  }
  return (
    <Wrapper className="section">
      <div className="title">
        <h2>featured services</h2>
        <div className="underline"></div>
      </div>
      <div className="section-center featured">
      <div class="row justify-content-around">
    <div class="col-4">
     <a href="Plumbing.html"> <img src="img/plumbing.jpg" alt="" style=" border-radius: 25px; "/></a>
    </div>
    <div class="col-4">
      <a href="#"> <img src="img/electrician.jpg" alt="" style=" border-radius: 25px;"/ ></a>
    </div>
    <div class="col-4">
      <a href="#"> <img src="img/painter.jpg" alt="" style=" border-radius: 25px;"/></a>
    </div>
  </div>
      </div>
      <Link to="/products" className="btn">
        services
      </Link>
    </Wrapper>
  );
};

const Wrapper = styled.section`
  background: var(--clr-grey-10);
  .featured {
    margin: 4rem auto;
    display: grid;
    gap: 2.5rem;
    img {
      height: 225px;
    }
  }
  .btn {
    display: block;
    width: 148px;
    margin: 0 auto;
    text-align: center;
  }
  @media (min-width: 576px) {
    .featured {
      grid-template-columns: repeat(auto-fit, minmax(360px, 1fr));
    }
  }
`;

export default FeaturedProducts;
